import React, { useState, useEffect } from 'react';
import './App.css';
import axios from 'axios';
import MapView from './MapView';

function CombinedTransportApp() {
  const [siriData, setSiriData] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [stationCode, setStationCode] = useState('26472'); // Default station code
  const [lineNumber, setLineNumber] = useState('60'); // Default line number
  const [routeShape, setRouteShape] = useState(null);
  const [routeDirection, setRouteDirection] = useState('0'); // 0 = outbound, 1 = inbound

  // Map state
  const [mapCenter, setMapCenter] = useState([32.07, 34.83]); // Default center around Tel Aviv
  const [vehicleMarkers, setVehicleMarkers] = useState([]);
  const [stops, setStops] = useState([]);

  const fetchStationData = () => {
    setLoading(true);
    setError(null);

    // Use direct axios call to the transport.php endpoint
    axios.get(`/transport.php?station=${stationCode}`)
      .then(response => {
        console.log('API Response:', response.data);
        // Store the raw SIRI data directly without transformation
        setSiriData(response.data);

        // Extract vehicle markers from SIRI data
        const monitoredStopVisits = response.data?.Siri?.ServiceDelivery?.StopMonitoringDelivery?.[0]?.MonitoredStopVisit || [];
        const markers = monitoredStopVisits.map(visit => {
          const journey = visit.MonitoredVehicleJourney || {};
          const vehicleLocation = journey.VehicleLocation || {};

          if (vehicleLocation.Latitude && vehicleLocation.Longitude) {
            return {
              position: [vehicleLocation.Latitude, vehicleLocation.Longitude],
              vehicleRef: journey.VehicleRef,
              lineNumber: journey.PublishedLineName,
              expectedArrival: journey.MonitoredCall?.ExpectedArrivalTime,
              distanceFromStop: journey.MonitoredCall?.DistanceFromStop
            };
          }
          return null;
        }).filter(Boolean);

        setVehicleMarkers(markers);
        setLoading(false);
      })
      .catch(err => {
        console.error('Error fetching arrivals:', err);
        setError(`Connection error: ${err.message}. Make sure the API server is running.`);
        setLoading(false);
      });
  };

  const fetchLineShape = () => {
    setLoading(true);
    axios.get(`/api/gtfs-api.php?action=getLineShape&line=${lineNumber}`)
      .then(response => {
        console.log('Shape data:', response.data);
        if (response.data.error) {
          setError(response.data.error);
          setRouteShape(null);
        } else {
          // Use the selected direction or default to '0' if not available
          const directionData = response.data[routeDirection] || response.data['0'] || [];
          setRouteShape(directionData);

          // If we have shape data, center the map on the first point
          if (directionData.length > 0) {
            setMapCenter(directionData[0]);
          }
        }
        setLoading(false);
      })
      .catch(err => {
        console.error('Error fetching line shape:', err);
        setError(`Connection error: ${err.message}`);
        setLoading(false);
      });
  };

  useEffect(() => {
    // Fetch initial data
    fetchStationData();
  }, [stationCode]);

  function ShowData({ siriData, loading, error }) {
    if (loading) return <h2>Loading...</h2>;
    if (error) return <h2>Error: {error}</h2>;
    if (!siriData) return <h2>No data available</h2>;

    const monitoredStopVisits = siriData?.Siri?.ServiceDelivery?.StopMonitoringDelivery?.[0]?.MonitoredStopVisit || [];

    if (monitoredStopVisits.length === 0) {
      return <h2>No vehicles are currently being monitored for station {stationCode}</h2>;
    }

    return (
      <div>
        <h2>Monitored Vehicles:</h2>
        <table className="vehicle-table">
          <thead>
            <tr>
              <th>Line</th>
              <th>Direction</th>
              <th>Destination</th>
              <th>Arrival Time</th>
              <th>Vehicle</th>
              <th>Distance</th>
            </tr>
          </thead>
          <tbody>
            {monitoredStopVisits.map((visit, index) => {
              const journey = visit.MonitoredVehicleJourney || {};
              const monitoredCall = journey.MonitoredCall || {};

              // Format the expected arrival time
              let arrivalTime = "N/A";
              if (monitoredCall.ExpectedArrivalTime) {
                const date = new Date(monitoredCall.ExpectedArrivalTime);
                arrivalTime = date.toLocaleTimeString();
              }

              return (
                <tr key={visit.ItemIdentifier || index}>
                  <td>{journey.PublishedLineName || 'N/A'}</td>
                  <td>{journey.DirectionRef || 'N/A'}</td>
                  <td>{journey.DestinationRef || 'N/A'}</td>
                  <td>{arrivalTime}</td>
                  <td>{journey.VehicleRef || 'N/A'}</td>
                  <td>{monitoredCall.DistanceFromStop ? `${monitoredCall.DistanceFromStop} meters` : 'N/A'}</td>
                </tr>
              );
            })}
          </tbody>
        </table>
      </div>
    );
  }

  return (
    <div className="combined-app">
      <div className="app-header">
        <h1>Israel Public Transportation Tracker</h1>
      </div>
      <div className="controls-panel">
        <div style={{ marginRight: '20px' }}>
          <label style={{ marginRight: '10px' }}>Station:</label>
          <select 
            value={stationCode}
            onChange={(e) => setStationCode(e.target.value)}
            style={{ padding: '5px' }}
          >
            <option value="26472">מסוף עמידר (26472)</option>
            <option value="20832">Station 20832</option>
            {/* Add more stations as needed */}
          </select>
          <button 
            onClick={fetchStationData} 
            style={{ marginLeft: '10px', padding: '5px 10px' }}
          >
            Refresh Station Data
          </button>
        </div>
        <div>
          <label style={{ marginRight: '10px' }}>Line:</label>
          <input 
            type="text" 
            value={lineNumber} 
            onChange={(e) => setLineNumber(e.target.value)}
            style={{ width: '50px', padding: '5px' }}
          />
          <label style={{ marginLeft: '10px', marginRight: '5px' }}>Direction:</label>
          <select
            value={routeDirection}
            onChange={(e) => setRouteDirection(e.target.value)}
            style={{ padding: '5px' }}
          >
            <option value="0">Outbound</option>
            <option value="1">Inbound</option>
          </select>
          <button 
            onClick={fetchLineShape} 
            style={{ marginLeft: '10px', padding: '5px 10px' }}
          >
            Show Route
          </button>
        </div>
      </div>
      <div className="main-content">
        <div className="map-section">
          <MapView
            latitude={mapCenter[0]}
            longitude={mapCenter[1]}
            mapCenter={mapCenter}
            vehicleMarkers={vehicleMarkers}
            routeShape={routeShape}
            stops={stops}
            onDestinationSet={() => {}}  // Placeholder
            center={mapCenter}
          />
        </div>
        <div className="data-section">
          <ShowData siriData={siriData} loading={loading} error={error} />
        </div>
      </div>
    </div>
  );
}

export default CombinedTransportApp;
