import { useEffect, useState, useMemo } from 'react';
import './App.css';
import { MapContainer, TileLayer, Marker, Popup } from 'react-leaflet';
import L from 'leaflet';
import 'leaflet/dist/leaflet.css';
import axios from 'axios';
import { TrackMapMovement, UpdateMapView, fetchAddress } from './components/map/MapUtilities';
import { configureDefaultLeafletIcons, destinationIcon, centerIcon, vehicleIcon, stopIcon } from './components/map/MapMarkers';
import MapControls from './components/map/MapControls';
import { simplifyShape } from './utils/ShapeSimplifier';
import useMapHandlers from './hooks/useMapHandlers';
import RouteMapView from './components/map/RouteMapView';

// Configure default Leaflet icons
configureDefaultLeafletIcons();

function MapView({ 
  latitude, 
  longitude, 
  destination, 
  onDestinationSet, 
  startingPoint,
  mapCenter: initialMapCenter,
  vehicleMarkers = [],
  routeShape = null,
  stops = [],
  selectedStop = null,
  center 
}) {
  const [position, setPosition] = useState([latitude, longitude]);
  const [route, setRoute] = useState(null);
  const [mapReady, setMapReady] = useState(false);
  const [mapCenter, setMapCenter] = useState(center || initialMapCenter || [latitude, longitude]);
  const [showRoutePanel, setShowRoutePanel] = useState(false);

  // Use custom hook for map handlers
  const {
    searchQuery,
    setSearchQuery,
    searchError,
    positionAddress,
    destinationAddress,
    setPositionAddress,
    setDestinationAddress,
    handleSearch,
    handleSetStartPoint,
    handleSetDestinationPoint,
    handleKeyPress,
    handleFindRoute
  } = useMapHandlers(position, destination, onDestinationSet, setMapCenter, mapCenter);

  // Memoize and simplify the route shape to improve performance
  const optimizedRouteShape = useMemo(() => {
    if (!routeShape || !Array.isArray(routeShape) || routeShape.length < 3) {
      return routeShape;
    }

    console.log(`Simplifying route shape with ${routeShape.length} points...`);

    // Determine if we need to simplify (only for large routes)
    if (routeShape.length > 300) {
      // Stronger simplification for very large routes
      const factor = routeShape.length > 1000 ? 0.00015 : 0.0001;
      const simplified = simplifyShape(routeShape, factor);
      console.log(`Simplified to ${simplified.length} points (${Math.round(simplified.length/routeShape.length*100)}%)`);
      return simplified;
    }

    return routeShape;
  }, [routeShape]);

  // Log routeShape when it changes to debug
  useEffect(() => {
    if (routeShape) {
      console.log("Route shape received in MapView:", routeShape.length, "points");
      console.log("First point:", routeShape[0]);
      console.log("Last point:", routeShape[routeShape.length - 1]);
    }
  }, [routeShape]);

  // When destination changes, ensure it's properly set
  useEffect(() => {
    if (destination && Array.isArray(destination) && destination.length === 2) {
      // Destination is valid
      fetchAddress(destination[0], destination[1], setDestinationAddress);
    }
  }, [destination]);

  // When position changes
  useEffect(() => {
    if (position && Array.isArray(position) && position.length === 2) {
      fetchAddress(position[0], position[1], setPositionAddress);
    }
  }, [position]);

  // If we have a proper route shape, show the route panel with RouteMapView
  const handleShowRoutePanel = () => {
    if (optimizedRouteShape && optimizedRouteShape.length > 0) {
      setShowRoutePanel(true);
    }
  };

  // Handle clicking the "Find Route" button
  const handleFindRouteClick = async () => {
    if (destination) {
      const routeData = await handleFindRoute();
      if (routeData) {
        setRoute(routeData);
        console.log("Setting route with", routeData.length, "points");
      }
    } else {
      console.error("No destination set");
    }
  };

  return (
    <div style={{ height: '100vh', width: '100%' }}>
      <MapControls 
        searchQuery={searchQuery}
        setSearchQuery={setSearchQuery}
        handleSearch={handleSearch}
        handleKeyPress={handleKeyPress}
        handleSetStartPoint={handleSetStartPoint}
        handleSetDestinationPoint={handleSetDestinationPoint}
        handleFindRoute={handleFindRouteClick}
        searchError={searchError}
        positionAddress={positionAddress}
        destinationAddress={destinationAddress}
      />

      {/* Route panel for showing the shape */}
      {(showRoutePanel || optimizedRouteShape) && (
        <div style={{
          position: 'absolute',
          top: '10px',
          right: '10px',
          background: 'white',
          padding: '10px',
          zIndex: 1000,
          width: '300px',
          boxShadow: '0 0 10px rgba(0,0,0,0.2)',
          borderRadius: '5px'
        }}>
          <h3>Route Shape View</h3>
          <button onClick={() => setShowRoutePanel(false)}>Close</button>
          <div style={{ height: '300px', marginTop: '10px' }}>
            <RouteMapView routeShape={optimizedRouteShape} />
          </div>
        </div>
      )}

      {!showRoutePanel && optimizedRouteShape && (
        <button 
          onClick={handleShowRoutePanel}
          style={{
            position: 'absolute',
            bottom: '20px',
            right: '20px',
            zIndex: 1000,
            padding: '10px',
            background: 'blue',
            color: 'white',
            border: 'none',
            borderRadius: '5px'
          }}
        >
          Show Route Panel
        </button>
      )}

      <MapContainer 
        center={mapCenter} 
        zoom={13} 
        style={{ height: '100%', width: '100%' }} 
        zoomControl={false}
        whenReady={() => setMapReady(true)}
        preferCanvas={true} // Use canvas renderer for better performance
      >
        <TileLayer
          url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
          attribution='&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors'
        />
        <UpdateMapView position={mapCenter} />
        <TrackMapMovement setMapCenter={setMapCenter} />

        {/* User position marker */}
        <Marker position={position}>
          <Popup>
            Your location: {positionAddress}<br/>
            Coordinates: {position[0].toFixed(6)}, {position[1].toFixed(6)}
          </Popup>
        </Marker>

        {/* Destination marker */}
        {destination && (
          <Marker position={destination} icon={destinationIcon}>
            <Popup>
              <div>
                <strong>Destination:</strong> {destinationAddress}<br/>
                <strong>Coordinates:</strong> {destination[0].toFixed(6)}, {destination[1].toFixed(6)}
              </div>
            </Popup>
          </Marker>
        )}

        {/* Map center marker */}
        <Marker position={mapCenter} icon={centerIcon}>
          <Popup>
            <div>
              <strong>Map Center:</strong> {Array.isArray(mapCenter) ? `${mapCenter[0]?.toFixed(6)}, ${mapCenter[1]?.toFixed(6)}` : 'Invalid coordinates'}
            </div>
          </Popup>
        </Marker>

        {/* Route display */}
        {route && <RouteMapView routeShape={route} />}

        {/* Vehicle markers */}
        {vehicleMarkers.map((vehicle, index) => (
          <Marker 
            key={`vehicle-${index}`} 
            position={vehicle.position}
            icon={vehicleIcon}
          >
            <Popup>
              <div>
                <strong>Line:</strong> {vehicle.lineNumber}<br/>
                <strong>Vehicle:</strong> {vehicle.vehicleRef}<br/>
                <strong>Expected arrival:</strong> {vehicle.expectedArrival}<br/>
                <strong>Distance from stop:</strong> {vehicle.distanceFromStop} m
              </div>
            </Popup>
          </Marker>
        ))}

        {/* Stop markers */}
        {stops.map(stop => (
          <Marker 
            key={`stop-${stop.stopId}`} 
            position={[stop.lat, stop.lon]}
            icon={stop.stopId === selectedStop ? centerIcon : stopIcon}
          >
            <Popup>
              <div>
                <strong>Stop:</strong> {stop.stopName}<br/>
                <strong>ID:</strong> {stop.stopId}<br/>
                <button onClick={() => handleSetStartPoint([stop.lat, stop.lon])}>
                  Set as starting point
                </button>
              </div>
            </Popup>
          </Marker>
        ))}
      </MapContainer>
    </div>
  );
}

export default MapView;
