import React from 'react';
import './App.css';
import axios from 'axios';
import { useEffect, useState, useRef } from 'react';

function App() {
  const [monitoringRef, setMonitoringRef] = useState(''); // Input for station number
  const [Siri, setSiriData] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  const fetchData = () => {
    if (!monitoringRef) {
      alert('Please enter a MonitoringRef (station number).');
      return;
    }

    setLoading(true);
    setError(null);
    console.group('Fetch Data Process');
    console.log('Starting fetch at:', new Date().toISOString());
    axios({
      method: 'get',
      url: `${process.env.REACT_APP_API_URL || 'http://localhost/publicTransportation.php'}?monitoringRef=${monitoringRef}`,
      timeout: 10000,
    })
      .then((res) => {
        console.log('Full Axios Response:', res);
        console.log('Response Data:', res.data);
        const siriData = res.data?.Siri || {}; // Store the entire Siri object
        console.log('Extracted Siri Data:', siriData);
        setSiriData(siriData);
        setLoading(false);
      })
      .catch((err) => {
        console.error('Complete Error Object:', err);
        console.log('Error Details:', {
          message: err.message,
          code: err.code,
          response: err.response,
          request: err.request,
        });
        const errorMessage = err.response
          ? `Server responded with status ${err.response.status}`
          : err.request
          ? 'Network error: Unable to reach the server'
          : 'An unexpected error occurred';
        setError(errorMessage);
        setLoading(false);
      })
      .finally(() => {
        console.groupEnd();
      });
  };

  function ShowData({ Siri, loading, error }) {
    if (loading) return <h2>Loading...</h2>;
    if (error) return <h2>{error}</h2>;

    const serviceDelivery = Siri?.ServiceDelivery;
    if (!serviceDelivery) {
      console.warn('No ServiceDelivery found:', Siri);
      return <h2>No data available</h2>;
    }

    const monitoredVehicles =
      serviceDelivery.StopMonitoringDelivery[0]?.MonitoredStopVisit || [];

    if (monitoredVehicles.length === 0) {
      console.warn('No monitored vehicles found:', serviceDelivery);
      return <h2>No vehicles are currently being monitored</h2>;
    }

    return (
      <div>
        <h2>Monitored Vehicles:</h2>
        <table className="vehicle-table">
          <thead>
            <tr>
              <th>Item Identifier</th>
              <th>Recorded At Time</th>
              <th>Monitoring Ref</th>
              <th>Line Ref</th>
              <th>Direction Ref</th>
              <th>Published Line Name</th>
              <th>Operator Ref</th>
              <th>Destination Ref</th>
              <th>Origin Aimed Departure Time</th>
              <th>Confidence Level</th>
              <th>Bearing</th>
              <th>Velocity</th>
              <th>Vehicle Ref</th>
              <th>Stop Point Ref</th>
              <th>Order</th>
              <th>Aimed Arrival Time</th>
              <th>Expected Arrival Time</th>
              <th>Distance From Stop</th>
              <th>Vehicle Location</th>
            </tr>
          </thead>
          <tbody>
            {monitoredVehicles.map((visit, index) => {
              const journey = visit.MonitoredVehicleJourney;
              const vehicleLocation = journey.VehicleLocation || {};
              return (
                <tr key={visit.ItemIdentifier || index}>
                  <td>{visit.ItemIdentifier}</td>
                  <td>{visit.RecordedAtTime}</td>
                  <td>{visit.MonitoringRef}</td>
                  <td>{journey.LineRef}</td>
                  <td>{journey.DirectionRef}</td>
                  <td>{journey.PublishedLineName}</td>
                  <td>{journey.OperatorRef}</td>
                  <td>{journey.DestinationRef}</td>
                  <td>{journey.OriginAimedDepartureTime}</td>
                  <td>{journey.ConfidenceLevel}</td>
                  <td>{journey.Bearing}</td>
                  <td>{journey.Velocity}</td>
                  <td>{journey.VehicleRef}</td>
                  <td>{journey.MonitoredCall?.StopPointRef || 'N/A'}</td>
                  <td>{journey.MonitoredCall?.Order || 'N/A'}</td>
                  <td>{journey.MonitoredCall?.AimedArrivalTime || 'N/A'}</td>
                  <td>{journey.MonitoredCall?.ExpectedArrivalTime || 'N/A'}</td>
                  <td>{journey.MonitoredCall?.DistanceFromStop ? `${journey.MonitoredCall.DistanceFromStop} meters` : 'N/A'}</td>
                  <td>
                    {vehicleLocation.Latitude && vehicleLocation.Longitude
                      ? `${vehicleLocation.Latitude}, ${vehicleLocation.Longitude}`
                      : 'Location not available'}
                  </td>
                </tr>
              );
            })}
          </tbody>
        </table>
      </div>
    );
  }

  return (
    <div className="App">
      <div className="input-container">
        <label htmlFor="monitoringRef">Enter MonitoringRef (Station Number):</label>
        <input
          id="monitoringRef"
          type="text"
          value={monitoringRef}
          onChange={(e) => setMonitoringRef(e.target.value)}
        />
        <button
          className="fetch-button"
          onClick={fetchData}
          disabled={loading}
        >
          {loading ? 'Fetching...' : 'Fetch Data'}
        </button>
      </div>
      <div className="content">
        <ShowData Siri={Siri} loading={loading} error={error} />
      </div>
    </div>
  );
}

export default App;
