import React, { useState, useEffect } from 'react';
import './App.css';
import axios from 'axios';
import { getStationArrivals, getLineArrivalsAtStation } from './api-service';

function App() {
  const [Siri, setSiriData] = useState({});
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [stationCode, setStationCode] = useState('26472'); // Default station code

  const fetchData = () => {
    setLoading(true);
    setError(null);
    console.group('Fetch Data Process');
    console.log('Starting fetch at:', new Date().toISOString());

    getStationArrivals(stationCode)
      .then(data => {
        console.log('Processed Arrivals Data:', data);
        if (Array.isArray(data) && data.length > 0) {
          setSiriData({ 
            ServiceDelivery: { 
              StopMonitoringDelivery: [{ MonitoredStopVisit: data }] 
            } 
          });
        } else {
          // Handle empty data case
          setSiriData({
            ServiceDelivery: {
              StopMonitoringDelivery: [{ MonitoredStopVisit: [] }]
            }
          });
          console.warn('No arrivals data available for this station');
        }
        setLoading(false);
      })
      .catch(err => {
        console.error('Error fetching arrivals:', err);
        setError(err.message || 'An unexpected error occurred');
        // Initialize with empty structure to avoid rendering errors
        setSiriData({
          ServiceDelivery: {
            StopMonitoringDelivery: [{ MonitoredStopVisit: [] }]
          }
        });
        setLoading(false);
      })
      .finally(() => {
        console.groupEnd();
      });
  };

  useEffect(() => {
    console.log('Initial Render - Fetching Data');
    fetchData();
  }, [stationCode]);

  function ShowData({ Siri, loading, error }) {
    if (loading) return <h2>Loading...</h2>;
    if (error) return <h2>Error: {error}</h2>;

    const serviceDelivery = Siri?.ServiceDelivery;
    if (!serviceDelivery) {
      console.warn('No ServiceDelivery found:', Siri);
      return <h2>No data available</h2>;
    }

    const monitoredVehicles =
      serviceDelivery.StopMonitoringDelivery[0]?.MonitoredStopVisit || [];

    if (monitoredVehicles.length === 0) {
      console.warn('No monitored vehicles found:', serviceDelivery);
      return <h2>No vehicles are currently being monitored for station {stationCode}</h2>;
    }

    return (
      <div>
        <h2>Monitored Vehicles:</h2>
        <table className="vehicle-table">
          <thead>
            <tr>
              <th>Item Identifier</th>
              <th>Recorded At Time</th>
              <th>Monitoring Ref</th>
              <th>Line Ref</th>
              <th>Direction Ref</th>
              <th>Published Line Name</th>
              <th>Operator Ref</th>
              <th>Destination Ref</th>
              <th>Origin Aimed Departure Time</th>
              <th>Confidence Level</th>
              <th>Bearing</th>
              <th>Velocity</th>
              <th>Vehicle Ref</th>
              <th>Stop Point Ref</th>
              <th>Order</th>
              <th>Aimed Arrival Time</th>
              <th>Expected Arrival Time</th>
              <th>Distance From Stop</th>
              <th>Vehicle Location</th>
            </tr>
          </thead>
          <tbody>
            {monitoredVehicles.map((visit, index) => {
              const journey = visit.MonitoredVehicleJourney;
              const vehicleLocation = journey.VehicleLocation || {};
              return (
                <tr key={visit.ItemIdentifier || index}>
                  <td>{visit.ItemIdentifier}</td>
                  <td>{visit.RecordedAtTime}</td>
                  <td>{visit.MonitoringRef}</td>
                  <td>{journey.LineRef}</td>
                  <td>{journey.DirectionRef}</td>
                  <td>{journey.PublishedLineName}</td>
                  <td>{journey.OperatorRef}</td>
                  <td>{journey.DestinationRef}</td>
                  <td>{journey.OriginAimedDepartureTime}</td>
                  <td>{journey.ConfidenceLevel}</td>
                  <td>{journey.Bearing}</td>
                  <td>{journey.Velocity}</td>
                  <td>{journey.VehicleRef}</td>
                  <td>{journey.MonitoredCall?.StopPointRef || 'N/A'}</td>
                  <td>{journey.MonitoredCall?.Order || 'N/A'}</td>
                  <td>{journey.MonitoredCall?.AimedArrivalTime || 'N/A'}</td>
                  <td>{journey.MonitoredCall?.ExpectedArrivalTime || 'N/A'}</td>
                  <td>{journey.MonitoredCall?.DistanceFromStop ? `${journey.MonitoredCall.DistanceFromStop} meters` : 'N/A'}</td>
                  <td>
                    {vehicleLocation.Latitude && vehicleLocation.Longitude
                      ? `${vehicleLocation.Latitude}, ${vehicleLocation.Longitude}`
                      : 'Location not available'}
                  </td>
                </tr>
              );
            })}
          </tbody>
        </table>
      </div>
    );
  }

  return (
    <div className="App">
      <div className="station-selector">
        <h3>Select Station</h3>
        <select 
          value={stationCode}
          onChange={(e) => setStationCode(e.target.value)}
        >
          <option value="26472">מסוף עמידר (26472)</option>
          <option value="20832">Station 20832</option>
          {/* Add more stations as needed */}
        </select>
        <button onClick={fetchData}>Refresh Data</button>
      </div>
      <div className="content">
        <ShowData Siri={Siri} loading={loading} error={error} />
      </div>
    </div>
  );
}

export default App;
