import React, { useState, useEffect } from 'react';
import './App.css';
import MapView from './MapView';
import * as transportService from './services/transportation-service';
import * as gtfsService from './services/gtfs-service';

function CombinedTransportApp() {
  // State for transportation data
  const [stationData, setStationData] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [stationCode, setStationCode] = useState('26472');
  const [stations, setStations] = useState([]);
  const [routes, setRoutes] = useState([]);

  // State for map
  const [startingPoint, setStartingPoint] = useState([32.06948949599059, 34.83984033547383]);
  const [destination, setDestination] = useState([32.06764959792775, 34.7867112130343]);
  const [selectedRoute, setSelectedRoute] = useState(null);
  const [routeShape, setRouteShape] = useState(null);

  // Load reference data (stops, routes) on component mount
  useEffect(() => {
    async function loadReferenceData() {
      try {
        // Load list of stations
        const stopsData = await gtfsService.getStops();
        // Ensure stopsData is an array
        setStations(Array.isArray(stopsData) ? stopsData : []);

        // Load list of routes
        const routesData = await gtfsService.getRoutes();
        // Ensure routesData is an array
        setRoutes(Array.isArray(routesData) ? routesData : []);
      } catch (err) {
        console.error('Error loading reference data:', err);
        // Initialize with empty arrays on error
        setStations([]);
        setRoutes([]);
      }
    }

    loadReferenceData();
  }, []);

  // Fetch station data when station changes
  useEffect(() => {
    fetchStationData();
  }, [stationCode]);

  // Fetch route shape when selected route changes
  useEffect(() => {
    if (selectedRoute) {
      fetchRouteShape(selectedRoute);
    }
  }, [selectedRoute]);

  // Fetch data for the selected station
  const fetchStationData = async () => {
    setLoading(true);
    setError(null);

    try {
      const data = await transportService.getStationData(stationCode);
      setStationData(data);

      // If we have station coordinates, center the map there
      const stationDetails = await transportService.getStopDetails(stationCode);
      if (stationDetails && stationDetails.lat && stationDetails.lon) {
        setStartingPoint([stationDetails.lat, stationDetails.lon]);
      }

      setLoading(false);
    } catch (err) {
      console.error('Error fetching station data:', err);
      setError(`Failed to load station data: ${err.message}`);
      setLoading(false);
    }
  };

  // Fetch shape data for a route
  const fetchRouteShape = async (routeId) => {
    try {
      const shapeData = await gtfsService.getRouteShape(routeId);
      setRouteShape(shapeData);
    } catch (err) {
      console.error('Error fetching route shape:', err);
    }
  };

  // Component for displaying combined schedule/real-time data
  function TransportationDataView({ stationData, loading, error }) {
    if (loading) return <h2>Loading...</h2>;
    if (error) return <h2>Error: {error}</h2>;
    if (!stationData) return <h2>No data available</h2>;

    const { combinedData } = stationData;

    if (combinedData.length === 0) {
      return <h2>No upcoming departures for station {stationCode}</h2>;
    }

    return (
      <div className="transport-data">
        <h2>Upcoming Departures</h2>
        <table className="vehicle-table">
          <thead>
            <tr>
              <th>Line</th>
              <th>Destination</th>
              <th>Scheduled</th>
              <th>Expected</th>
              <th>Status</th>
              <th>Distance</th>
              <th>Vehicle</th>
            </tr>
          </thead>
          <tbody>
            {combinedData.map((item, index) => {
              // Calculate status text based on delay
              let statusText = 'On time';
              let statusClass = 'status-ontime';

              if (item.realTime) {
                if (item.delay > 2) {
                  statusText = `Late ${item.delay} min`;
                  statusClass = 'status-late';
                } else if (item.delay < -2) {
                  statusText = `Early ${Math.abs(item.delay)} min`;
                  statusClass = 'status-early';
                }
              } else {
                statusText = 'Scheduled';
                statusClass = 'status-scheduled';
              }

              // Format times
              const scheduledTime = item.scheduledArrival ? new Date(item.scheduledArrival).toLocaleTimeString() : 'N/A';
              const expectedTime = item.realTime && item.expectedArrival ? 
                new Date(item.expectedArrival).toLocaleTimeString() : scheduledTime;

              return (
                <tr 
                  key={index} 
                  className={item.realTime ? 'has-realtime' : ''}
                  onClick={() => setSelectedRoute(item.routeId)}
                >
                  <td>{item.lineNumber}</td>
                  <td>{item.destination}</td>
                  <td>{scheduledTime}</td>
                  <td>{expectedTime}</td>
                  <td className={statusClass}>{statusText}</td>
                  <td>
                    {item.distanceFromStop ? `${item.distanceFromStop} m` : 'N/A'}
                  </td>
                  <td>{item.vehicleRef || 'N/A'}</td>
                </tr>
              );
            })}
          </tbody>
        </table>

        <div className="legend">
          <div className="legend-item">
            <span className="status-dot status-ontime"></span> On time
          </div>
          <div className="legend-item">
            <span className="status-dot status-early"></span> Early
          </div>
          <div className="legend-item">
            <span className="status-dot status-late"></span> Late
          </div>
          <div className="legend-item">
            <span className="status-dot status-scheduled"></span> Scheduled (no real-time data)
          </div>
        </div>
      </div>
    );
  }

  // Generate vehicle markers for the map
  const generateVehicleMarkers = () => {
    if (!stationData || !stationData.realTimeArrivals) return [];

    return stationData.realTimeArrivals
      .filter(arrival => 
        arrival.vehicleLocation && 
        arrival.vehicleLocation.lat && 
        arrival.vehicleLocation.lon
      )
      .map(arrival => ({
        position: [arrival.vehicleLocation.lat, arrival.vehicleLocation.lon],
        lineNumber: arrival.lineNumber,
        vehicleRef: arrival.vehicleRef,
        expectedArrival: arrival.expectedArrival ? 
          new Date(arrival.expectedArrival).toLocaleTimeString() : 'Unknown',
        distanceFromStop: arrival.distanceFromStop
      }));
  };

  return (
    <div className="combined-app">
      <header className="app-header">
        <h1>Israel Public Transportation Tracker</h1>
      </header>

      <div className="controls-panel">
        <div className="station-selector">
          <h3>Select Station</h3>
          <select 
            value={stationCode}
            onChange={(e) => setStationCode(e.target.value)}
          >
            {/* Ensure stations is an array before mapping */}
            {Array.isArray(stations) && stations.length > 0 ? (
              stations.map(station => (
                <option key={station.stopId} value={station.stopId}>
                  {station.stopName} ({station.stopId})
                </option>
              ))
            ) : (
              <>
                <option value="26472">מסוף עמידר (26472)</option>
                <option value="20832">Station 20832</option>
              </>
            )}
          </select>

          <button onClick={fetchStationData} disabled={loading}>
            {loading ? 'Refreshing...' : 'Refresh Data'}
          </button>
        </div>

        <div className="route-selector">
          <h3>Select Route</h3>
          <select 
            value={selectedRoute || ''}
            onChange={(e) => setSelectedRoute(e.target.value)}
          >
            <option value="">-- Select Route --</option>
            {/* Ensure routes is an array before mapping */}
            {Array.isArray(routes) && routes.map(route => (
              <option key={route.routeId} value={route.routeId}>
                {route.routeShortName} - {route.routeLongName}
              </option>
            ))}
          </select>
        </div>
      </div>

      <div className="main-content">
        <div className="map-section">
          <h2>Transportation Map</h2>
          <div className="map-container">
            <MapView
              latitude={startingPoint[0]}
              longitude={startingPoint[1]}
              destination={destination}
              onDestinationSet={setDestination}
              startingPoint={startingPoint}
              mapCenter={startingPoint} // Change from 'center' to 'mapCenter' to match the expected prop
              zoom={14}
              vehicleMarkers={generateVehicleMarkers()}
              routeShape={routeShape}
              stops={stations.filter(station => 
                // Filter to show only stops near the current view
                Math.abs(station.lat - startingPoint[0]) < 0.05 && 
                Math.abs(station.lon - startingPoint[1]) < 0.05
              )}
              selectedStop={stationCode}
            />
          </div>
        </div>

        <div className="data-section">
          <TransportationDataView 
            stationData={stationData}
            loading={loading}
            error={error}
          />
        </div>
      </div>
    </div>
  );
}

export default CombinedTransportApp;
