import './App.css';
import axios from 'axios';
import { useEffect, useState } from 'react';

function App() {
  const [Siri, setSiriData] = useState([]);
  const [loading, setLoading] = useState(false);

  const fetchData = () => {
    setLoading(true);
    axios
      .get('https://localhost/publicTransportation.php')
      .then((res) => {
        setSiriData(res.data.Siri || { message: 'No data available' });
        setLoading(false);
      })
      .catch((err) => {
        console.error('Error fetching data:', err);
        setLoading(false);
      });
  };

  useEffect(() => {
    fetchData();
  }, []); // Fetch data only on initial render

  function VehicleDetails({ journey }) {
    return (
      <div className="vehicle-details-container">
        <div className="details-row">
          <label className="label">Line:</label>
          <span className="value">{journey.PublishedLineName}</span>
        </div>
        <div className="details-row">
          <label className="label">Direction:</label>
          <span className="value">{journey.DirectionRef}</span>
        </div>
        <div className="details-row">
          <label className="label">Expected Arrival:</label>
          <span className="value">{journey.MonitoredCall.ExpectedArrivalTime}</span>
        </div>
        <div className="details-row">
          <label className="label">Distance From Stop:</label>
          <span className="value">{journey.MonitoredCall.DistanceFromStop} meters</span>
        </div>
        <div className="details-row">
          <label className="label">Vehicle Location:</label>
          <span className="value">
            {journey.VehicleLocation.Latitude}, {journey.VehicleLocation.Longitude}
          </span>
        </div>
      </div>
    );
  }

  function ShowData({ Siri, loading }) {
    if (loading) {
      return <h2>Loading...</h2>;
    }
    if (!Siri || !Siri.ServiceDelivery) {
      return <h2>No data available</h2>;
    }
    const monitoredVehicles =
      Siri.ServiceDelivery.StopMonitoringDelivery[0]?.MonitoredStopVisit || [];
    return (
      <div>
        <h2>Monitored Vehicles:</h2>
        {monitoredVehicles.map((visit, index) => (
          <VehicleDetails
            key={visit.ItemIdentifier || index}
            journey={visit.MonitoredVehicleJourney}
          />
        ))}
      </div>
    );
  }

  return (
    <div className="App">
      <button className="refresh-button" onClick={fetchData}>Refresh</button>
      <div className="content">
        <div className="left-side">
          <ShowData Siri={Siri} loading={loading} />
        </div>
        <div className="right-side">
          {console.log(Siri)}
          <h2>Map</h2>
        </div>
      </div>
    </div>
  );
}

export default App;
