import './App.css';
import axios from 'axios';
import { useEffect, useState, useRef } from 'react';
import MapView from './MapView';

// Move these components outside of App function
function VehicleDetails({ journey }) {
  return (
    <div className="vehicle-details-container">
      <div className="details-row">
        <label className="label">Line:</label>
        <span className="value">{journey.PublishedLineName}</span>
      </div>
      <div className="details-row">
        <label className="label">Direction:</label>
        <span className="value">{journey.DirectionRef}</span>
      </div>
      <div className="details-row">
        <label className="label">Expected Arrival:</label>
        <span className="value">{journey.MonitoredCall.ExpectedArrivalTime}</span>
      </div>
      <div className="details-row">
        <label className="label">Distance From Stop:</label>
        <span className="value">{journey.MonitoredCall.DistanceFromStop} meters</span>
      </div>
      <div className="details-row">
        <label className="label">Vehicle Location:</label>
        <span className="value">
          {journey.VehicleLocation.Latitude}, {journey.VehicleLocation.Longitude}
        </span>
      </div>
    </div>
  );
}

function ShowData({ siriData, loading, error }) {
  if (loading) return <h2>Loading...</h2>;
  if (error) return <h2>{error}</h2>;

  if (!siriData || !siriData.ServiceDelivery) {
    console.warn('No Siri data or ServiceDelivery found');
    return <h2>No data available</h2>;
  }

  const monitoredVehicles =
    siriData.ServiceDelivery.StopMonitoringDelivery[0]?.MonitoredStopVisit || [];

  if (monitoredVehicles.length === 0) {
    return <h2>No vehicles are currently being monitored</h2>;
  }

  console.log('Monitored Vehicles:', monitoredVehicles);

  return (
    <div>
      <h2>Monitored Vehicles:</h2>
      {monitoredVehicles.map((visit, index) => (
        <VehicleDetails
          key={visit.ItemIdentifier || index}
          journey={visit.MonitoredVehicleJourney}
        />
      ))}
    </div>
  );
}

function App() {
  const [location, setLocation] = useState({ latitude: 32.075249, longitude: 34.807137 });
  const isInitialRender = useRef(true);
  const [siriData, setSiriData] = useState({}); // Initialize as object, not array
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [showMap, setShowMap] = useState(true);

  // Simulate location change after 5 seconds
  useEffect(() => {
    setTimeout(() => {
      setLocation({ latitude: 32.06928802451271, longitude: 34.83981370925904 });
    }, 5000);
  }, []);

  useEffect(() => {
    if (isInitialRender.current) {
      isInitialRender.current = false; 
      return;
    }
    console.log('Hi');
  }, []); 

  const fetchData = () => {
    setLoading(true);
    setError(null);

    console.group('Fetch Data Process');
    console.log('Starting fetch at:', new Date().toISOString());

    axios({
      method: 'get',
      url: process.env.REACT_APP_API_URL || 'https://localhost/publicTransportation.php',
      timeout: 10000, // 10-second timeout
    })
      .then((res) => {
        console.log('Full Axios Response:', res);
        console.log('Response Data:', res.data);

        setSiriData(res.data);
        setLoading(false);
      })
      .catch((err) => {
        console.error('Complete Error Object:', err);
        console.log('Error Details:', {
          message: err.message,
          code: err.code,
          response: err.response,
          request: err.request,
        });

        const errorMessage = err.response
          ? `Server responded with status ${err.response.status}`
          : err.request
          ? 'Network error: Unable to reach the server'
          : 'An unexpected error occurred';
        setError(errorMessage);
        setLoading(false);
      })
      .finally(() => {
        console.groupEnd();
      });
  };

  useEffect(() => {
    console.log('Initial Render - Fetching Data');
    fetchData();
  }, []);

  useEffect(() => {
    console.log('Siri State Updated:', siriData);
  }, [siriData]);

  return (
    <div className="App">
      <div className="control-panel">
        <button
          className="refresh-button"
          onClick={fetchData}
          disabled={loading}
        >
          {loading ? 'Refreshing...' : 'Refresh'}
        </button>
        <button 
          className="toggle-view-button"
          onClick={() => setShowMap(!showMap)}
        >
          {showMap ? 'Show Transportation Data' : 'Show Map'}
        </button>
      </div>

      {showMap ? (
        <MapView latitude={location.latitude} longitude={location.longitude} />
      ) : (
        <div className="content">
          <div className="left-side">
            <ShowData siriData={siriData} loading={loading} error={error} />
          </div>
          <div className="right-side">
            <h2>Additional Information</h2>
            {/* You can add additional views here */}
          </div>
        </div>
      )}
    </div>
  );
}

export default App;
