import { useEffect, useState } from 'react';
import './App.css';
import { MapContainer, TileLayer, Marker, Popup, useMapEvents, useMap } from 'react-leaflet'; // Added useMap
import 'leaflet/dist/leaflet.css';
import L from 'leaflet';
import axios from 'axios'; // Import axios for geocoding API

delete L.Icon.Default.prototype._getIconUrl;
L.Icon.Default.mergeOptions({
  iconRetinaUrl: 'https://cdnjs.cloudflare.com/ajax/libs/leaflet/1.7.1/images/marker-icon-2x.png',
  iconUrl: 'https://cdnjs.cloudflare.com/ajax/libs/leaflet/1.7.1/images/marker-icon.png',
  shadowUrl: 'https://cdnjs.cloudflare.com/ajax/libs/leaflet/1.7.1/images/marker-shadow.png',
});

// Component to track map movements and update position
function TrackMapMovement({ setPosition, isTrackingEnabled }) {
  useMapEvents({
    moveend: (event) => {
      if (isTrackingEnabled) {
        const map = event.target;
        const center = map.getCenter();
        setPosition([center.lat, center.lng]);
      }
    },
  });
  return null;
}

function UpdateMapView({ position }) {
  const map = useMap();
  useEffect(() => {
    map.setView(position, map.getZoom());
  }, [position, map]);
  return null;
}

function MapView({ latitude = 32.075249, longitude = 34.807137 }) {
  const [position, setPosition] = useState([latitude, longitude]);
  const [destination, setDestination] = useState(null); // State for destination
  const [isTrackingEnabled, setIsTrackingEnabled] = useState(true);
  const [searchQuery, setSearchQuery] = useState('');
  const [searchError, setSearchError] = useState(null);

  useEffect(() => {
    setPosition([latitude, longitude]);
  }, [latitude, longitude]);

  const handleSearch = async () => {
    if (!searchQuery.trim()) return;

    try {
      setSearchError(null);
      const response = await axios.get(
        `https://nominatim.openstreetmap.org/search?q=${encodeURIComponent(searchQuery)}&format=json`
      );
      if (response.data.length > 0) {
        const { lat, lon } = response.data[0];
        setPosition([parseFloat(lat), parseFloat(lon)]);
      } else {
        setSearchError('Location not found.');
      }
    } catch (error) {
      setSearchError('Error fetching location. Please try again.');
    }
  };

  const handleSetDestination = async () => {
    if (!searchQuery.trim()) return;

    try {
      setSearchError(null);
      const response = await axios.get(
        `https://nominatim.openstreetmap.org/search?q=${encodeURIComponent(searchQuery)}&format=json`
      );
      if (response.data.length > 0) {
        const { lat, lon } = response.data[0];
        setDestination([parseFloat(lat), parseFloat(lon)]);
      } else {
        setSearchError('Destination not found.');
      }
    } catch (error) {
      setSearchError('Error fetching destination. Please try again.');
    }
  };

  return (
    <div style={{ height: '100vh', width: '100%' }}>
      <div style={{ position: 'absolute', top: 10, left: 10, zIndex: 1000 }}>
        <button
          onClick={() => setIsTrackingEnabled((prev) => !prev)}
          style={{
            padding: '10px',
            backgroundColor: isTrackingEnabled ? 'green' : 'red',
            color: 'white',
            border: 'none',
            borderRadius: '5px',
            cursor: 'pointer',
            marginBottom: '10px',
          }}
        >
          {isTrackingEnabled ? 'Disable Tracking' : 'Enable Tracking'}
        </button>
        <div>
          <input
            type="text"
            placeholder="Search for a location"
            value={searchQuery}
            onChange={(e) => setSearchQuery(e.target.value)}
            style={{
              padding: '5px',
              width: '200px',
              marginRight: '5px',
              borderRadius: '5px',
              border: '1px solid #ccc',
            }}
          />
          <button
            onClick={handleSearch}
            style={{
              padding: '5px 10px',
              backgroundColor: 'blue',
              color: 'white',
              border: 'none',
              borderRadius: '5px',
              cursor: 'pointer',
              marginRight: '5px',
            }}
          >
            Search
          </button>
          <button
            onClick={handleSetDestination}
            style={{
              padding: '5px 10px',
              backgroundColor: 'orange',
              color: 'white',
              border: 'none',
              borderRadius: '5px',
              cursor: 'pointer',
            }}
          >
            Set Destination
          </button>
        </div>
        {searchError && <p style={{ color: 'red', marginTop: '5px' }}>{searchError}</p>}
      </div>
      <MapContainer center={position} zoom={13} style={{ height: '100%', width: '100%' }} zoomControl={false}>
        <TileLayer
          url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
          attribution='&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors'
        />
        <UpdateMapView position={position} />
        <TrackMapMovement setPosition={setPosition} isTrackingEnabled={isTrackingEnabled} />
        <Marker position={position}>
          <Popup>
            Your location: {position[0]}, {position[1]}
          </Popup>
        </Marker>
        {destination && (
          <Marker position={destination} icon={L.icon({ iconUrl: 'https://cdnjs.cloudflare.com/ajax/libs/leaflet/1.7.1/images/marker-icon-red.png' })}>
            <Popup>
              Destination: {destination[0]}, {destination[1]}
            </Popup>
          </Marker>
        )}
      </MapContainer>
    </div>
  );
}

export default MapView;
