import { useEffect, useState } from 'react';
import './App.css';
import { MapContainer, TileLayer, Marker, Popup, Polyline, useMap } from 'react-leaflet';
import L from 'leaflet';  // Added this import to fix the 'L is not defined' error
import 'leaflet/dist/leaflet.css';
import axios from 'axios';
import { TrackMapMovement, UpdateMapView, fetchAddress, findRoute } from './components/map/MapUtilities';
import { configureDefaultLeafletIcons, destinationIcon, centerIcon, vehicleIcon, stopIcon } from './components/map/MapMarkers';
import MapControls from './components/map/MapControls';

// Configure default Leaflet icons
configureDefaultLeafletIcons();

function MapView({ 
  latitude, 
  longitude, 
  destination, 
  onDestinationSet, 
  startingPoint,
  mapCenter: initialMapCenter,
  vehicleMarkers = [],
  routeShape = null,
  stops = [],
  selectedStop = null,
  center 
}) {
  const [position, setPosition] = useState([latitude, longitude]);
  const [positionAddress, setPositionAddress] = useState('');
  const [destinationAddress, setDestinationAddress] = useState('');
  const [route, setRoute] = useState(null);
  const [searchQuery, setSearchQuery] = useState('');
  const [searchError, setSearchError] = useState(null);
  const [mapReady, setMapReady] = useState(false);

  const [mapCenter, setMapCenter] = useState(center || initialMapCenter || [latitude, longitude]);

  useEffect(() => {
    const style = document.createElement('style');
    style.innerHTML = `
      .destination-marker {
        filter: hue-rotate(120deg);
      }
    `;
    document.head.appendChild(style);
    return () => {
      document.head.removeChild(style);
    };
  }, []);

  useEffect(() => {
    setPosition([latitude, longitude]);
    fetchAddress(latitude, longitude, setPositionAddress);
  }, [latitude, longitude]);

  useEffect(() => {
    if (destination) {
      const newCenter = [
        (position[0] + destination[0]) / 2,
        (position[1] + destination[1]) / 2
      ];
      setMapCenter(newCenter);
      fetchAddress(destination[0], destination[1], setDestinationAddress);
    }
  }, [position, destination]);

  // Log routeShape when it changes to debug
  useEffect(() => {
    if (routeShape) {
      console.log("Route shape received in MapView:", routeShape.length, "points");
      console.log("First point:", routeShape[0]);
      console.log("Last point:", routeShape[routeShape.length - 1]);
    }
  }, [routeShape]);

  // MapEffect component to update view and zoom when routeShape changes
  const MapEffect = ({ routeShape }) => {
    const map = useMap();

    useEffect(() => {
      if (routeShape && routeShape.length > 0) {
        try {
          // Create a bounds object from the route shape
          const bounds = routeShape.reduce((bounds, point) => {
            bounds.extend(point);
            return bounds;
          }, L.latLngBounds(routeShape[0], routeShape[0]));

          // Fit the map to these bounds with some padding
          map.fitBounds(bounds, { padding: [50, 50] });
          console.log("Map fitted to route bounds");
        } catch (e) {
          console.error("Error fitting map to route:", e);
        }
      }
    }, [map, routeShape]);

    return null;
  };

  const handleSearch = async () => {
    if (!searchQuery.trim()) return;

    try {
      setSearchError(null);
      const response = await axios.get(
        `https://nominatim.openstreetmap.org/search?q=${encodeURIComponent(searchQuery)}&format=json`
      );
      if (response.data.length > 0) {
        const { lat, lon } = response.data[0];
        setMapCenter([parseFloat(lat), parseFloat(lon)]);
      } else {
        setSearchError('Location not found.');
      }
    } catch (error) {
      setSearchError('Error fetching location. Please try again.');
    }
  };

  const handleSetStartPoint = () => {
    setPosition(mapCenter);
    fetchAddress(mapCenter[0], mapCenter[1], setPositionAddress);
  };

  const handleSetDestinationPoint = () => {
    const newDestination = mapCenter;
    onDestinationSet(newDestination);
    fetchAddress(newDestination[0], newDestination[1], setDestinationAddress);
  };

  const handleKeyPress = (e) => {
    if (e.key === 'Enter') {
      handleSearch();
    }
  };

  const handleFindRoute = async () => {
    if (!destination) {
      setSearchError('Please set a destination first.');
      return;
    }

    const MAX_RETRIES = 3;
    let attempts = 0;

    while (attempts < MAX_RETRIES) {
      try {
        setSearchError(null);
        const routeData = await findRoute(position, destination);

        if (routeData) {
          setRoute(routeData);
          return;
        } else {
          setSearchError('No route found. Please try again with different locations.');
          return;
        }
      } catch (error) {
        attempts++;
        if (attempts >= MAX_RETRIES) {
          if (error.response && error.response.status === 403) {
            setSearchError('Invalid API key or insufficient permissions.');
          } else if (error.response && error.response.status === 503) {
            setSearchError('Service unavailable. Please try again later.');
          } else {
            setSearchError('Error fetching route. Please try again.');
          }
        }
      }
    }
  };

  return (
    <div style={{ height: '100vh', width: '100%' }}>
      <MapControls 
        searchQuery={searchQuery}
        setSearchQuery={setSearchQuery}
        handleSearch={handleSearch}
        handleKeyPress={handleKeyPress}
        handleSetStartPoint={handleSetStartPoint}
        handleSetDestinationPoint={handleSetDestinationPoint}
        handleFindRoute={handleFindRoute}
        searchError={searchError}
        positionAddress={positionAddress}
        destinationAddress={destinationAddress}
      />

      <MapContainer 
        center={mapCenter} 
        zoom={13} 
        style={{ height: '100%', width: '100%' }} 
        zoomControl={false}
        whenReady={() => setMapReady(true)}
      >
        <TileLayer
          url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
          attribution='&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors'
        />
        <UpdateMapView position={mapCenter} />
        <TrackMapMovement setMapCenter={setMapCenter} />
        <MapEffect routeShape={routeShape} />

        {/* User position marker */}
        <Marker position={position}>
          <Popup>
            Your location: {positionAddress}<br/>
            Coordinates: {position[0].toFixed(6)}, {position[1].toFixed(6)}
          </Popup>
        </Marker>

        {/* Destination marker */}
        {destination && (
          <Marker position={destination} icon={destinationIcon}>
            <Popup>
              <div>
                <strong>Destination:</strong> {destinationAddress}<br/>
                <strong>Coordinates:</strong> {destination[0].toFixed(6)}, {destination[1].toFixed(6)}
              </div>
            </Popup>
          </Marker>
        )}

        {/* Map center marker */}
        <Marker position={mapCenter} icon={centerIcon}>
          <Popup>
            <div>
              <strong>Map Center:</strong> {Array.isArray(mapCenter) ? `${mapCenter[0]?.toFixed(6)}, ${mapCenter[1]?.toFixed(6)}` : 'Invalid coordinates'}
            </div>
          </Popup>
        </Marker>

        {/* Route display */}
        {route && <Polyline positions={route} color="blue" />}

        {/* Vehicle markers */}
        {vehicleMarkers.map((vehicle, index) => (
          <Marker 
            key={`vehicle-${index}`} 
            position={vehicle.position}
            icon={vehicleIcon}
          >
            <Popup>
              <div>
                <strong>Line:</strong> {vehicle.lineNumber}<br/>
                <strong>Vehicle:</strong> {vehicle.vehicleRef}<br/>
                <strong>Expected arrival:</strong> {vehicle.expectedArrival}<br/>
                <strong>Distance from stop:</strong> {vehicle.distanceFromStop} m
              </div>
            </Popup>
          </Marker>
        ))}

        {/* Route shape if available */}
        {routeShape && routeShape.length > 0 && (
          <Polyline 
            positions={routeShape} 
            pathOptions={{ 
              color: 'red', 
              weight: 3, 
              opacity: 0.7,
              dashArray: '5, 5',
              lineCap: 'round'
            }} 
          />
        )}

        {/* Stop markers */}
        {stops.map(stop => (
          <Marker 
            key={`stop-${stop.stopId}`} 
            position={[stop.lat, stop.lon]}
            icon={stop.stopId === selectedStop ? centerIcon : stopIcon}
          >
            <Popup>
              <div>
                <strong>Stop:</strong> {stop.stopName}<br/>
                <strong>ID:</strong> {stop.stopId}<br/>
                <button onClick={() => handleSetStartPoint([stop.lat, stop.lon])}>
                  Set as starting point
                </button>
              </div>
            </Popup>
          </Marker>
        ))}
      </MapContainer>
    </div>
  );
}

export default MapView;
