import React, { useState, useEffect } from 'react';
import './App.css';
import axios from 'axios';
import MapView from './MapView';

function CombinedTransportApp() {
  // State for transportation data
  const [siriData, setSiriData] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [stationCode, setStationCode] = useState('26472');

  // State for map
  const [startingPoint, setStartingPoint] = useState([32.06948949599059, 34.83984033547383]);
  const [destination, setDestination] = useState([32.06764959792775, 34.7867112130343]);
  const [mapCenter, setMapCenter] = useState([
    (startingPoint[0] + destination[0]) / 2,
    (startingPoint[1] + destination[1]) / 2,
  ]);

  // Update map center when starting point or destination changes
  useEffect(() => {
    setMapCenter([
      (startingPoint[0] + destination[0]) / 2,
      (startingPoint[1] + destination[1]) / 2,
    ]);
  }, [startingPoint, destination]);

  // Fetch transportation data
  const fetchData = () => {
    setLoading(true);
    setError(null);
    console.group('Fetch Data Process');
    console.log('Starting fetch at:', new Date().toISOString());

    // Use direct axios call to the transport.php endpoint
    axios.get(`/transport.php?station=${stationCode}`)
      .then(response => {
        console.log('API Response:', response.data);
        // Store the raw SIRI data directly
        setSiriData(response.data);
        setLoading(false);
      })
      .catch(err => {
        console.error('Error fetching arrivals:', err);
        setError(`Connection error: ${err.message}. Make sure the API server is running.`);
        setLoading(false);
      })
      .finally(() => {
        console.groupEnd();
      });
  };

  // Load data when component mounts or station changes
  useEffect(() => {
    console.log('Initial Render - Fetching Data');
    fetchData();
  }, [stationCode]);

  // Transportation data display component
  function TransportationDataView({ siriData, loading, error }) {
    if (loading) return <h2>Loading...</h2>;
    if (error) return <h2>Error: {error}</h2>;
    if (!siriData) return <h2>No data available</h2>;

    const monitoredStopVisits = siriData?.Siri?.ServiceDelivery?.StopMonitoringDelivery?.[0]?.MonitoredStopVisit || [];

    if (monitoredStopVisits.length === 0) {
      return <h2>No vehicles are currently being monitored for station {stationCode}</h2>;
    }

    return (
      <div>
        <h2>Monitored Vehicles:</h2>
        <table className="vehicle-table">
          <thead>
            <tr>
              <th>Line</th>
              <th>Direction</th>
              <th>Destination</th>
              <th>Arrival Time</th>
              <th>Vehicle</th>
              <th>Distance</th>
              <th>Location</th>
            </tr>
          </thead>
          <tbody>
            {monitoredStopVisits.map((visit, index) => {
              const journey = visit.MonitoredVehicleJourney || {};
              const vehicleLocation = journey.VehicleLocation || {};
              const monitoredCall = journey.MonitoredCall || {};

              // Format the expected arrival time
              let arrivalTime = "N/A";
              if (monitoredCall.ExpectedArrivalTime) {
                const date = new Date(monitoredCall.ExpectedArrivalTime);
                arrivalTime = date.toLocaleTimeString();
              }

              return (
                <tr key={visit.ItemIdentifier || index}>
                  <td>{journey.PublishedLineName || 'N/A'}</td>
                  <td>{journey.DirectionRef || 'N/A'}</td>
                  <td>{journey.DestinationRef || 'N/A'}</td>
                  <td>{arrivalTime}</td>
                  <td>{journey.VehicleRef || 'N/A'}</td>
                  <td>{monitoredCall.DistanceFromStop ? `${monitoredCall.DistanceFromStop} meters` : 'N/A'}</td>
                  <td>
                    {vehicleLocation.Latitude && vehicleLocation.Longitude
                      ? `${vehicleLocation.Latitude}, ${vehicleLocation.Longitude}`
                      : 'Location not available'}
                  </td>
                </tr>
              );
            })}
          </tbody>
        </table>
      </div>
    );
  }

  // Show vehicle details on the map where buses/vehicles are located
  const renderVehiclesOnMap = () => {
    if (!siriData) return null;

    const monitoredStopVisits = siriData?.Siri?.ServiceDelivery?.StopMonitoringDelivery?.[0]?.MonitoredStopVisit || [];

    // We could add markers on the map for each vehicle with location data
    // This would be implemented in MapView component

    // Let's extract vehicle locations to potentially use them
    const vehicleLocations = monitoredStopVisits
      .map(visit => {
        const journey = visit.MonitoredVehicleJourney || {};
        const vehicleLocation = journey.VehicleLocation || {};

        if (vehicleLocation.Latitude && vehicleLocation.Longitude) {
          return {
            id: visit.ItemIdentifier || `vehicle-${Math.random()}`,
            position: [parseFloat(vehicleLocation.Latitude), parseFloat(vehicleLocation.Longitude)],
            lineNumber: journey.PublishedLineName,
            vehicleRef: journey.VehicleRef,
            expectedArrival: journey.MonitoredCall?.ExpectedArrivalTime
          };
        }
        return null;
      })
      .filter(location => location !== null);

    // These locations could be passed to MapView for rendering
    console.log('Vehicle Locations:', vehicleLocations);
    return vehicleLocations;
  };

  return (
    <div className="combined-app">
      <header className="app-header">
        <h1>Israel Public Transportation Tracker</h1>
      </header>

      <div className="controls-panel">
        <div className="station-selector">
          <h3>Select Station</h3>
          <select 
            value={stationCode}
            onChange={(e) => setStationCode(e.target.value)}
          >
            <option value="26472">מסוף עמידר (26472)</option>
            <option value="20832">Station 20832</option>
            {/* Add more stations as needed */}
          </select>
          <button onClick={fetchData} disabled={loading}>
            {loading ? 'Refreshing...' : 'Refresh Data'}
          </button>
        </div>
      </div>

      <div className="main-content">
        <div className="map-section">
          <h2>Transportation Map</h2>
          <div className="map-container">
            <MapView
              latitude={startingPoint[0]}
              longitude={startingPoint[1]}
              destination={destination}
              onDestinationSet={setDestination}
              startingPoint={startingPoint}
              destinationCoords={destination}
              tooltipContent={`Your location: ${startingPoint[0]}, ${startingPoint[1]}`}
              // We could pass vehicle locations here to show on map
              vehicleLocations={renderVehiclesOnMap()}
            />
          </div>
        </div>

        <div className="data-section">
          <TransportationDataView 
            siriData={siriData} 
            loading={loading} 
            error={error} 
          />
        </div>
      </div>
    </div>
  );
}

export default CombinedTransportApp;
