import { useEffect, useState } from 'react';
import './App.css';
import { MapContainer, TileLayer, Marker, Popup, useMapEvents, useMap } from 'react-leaflet';
import 'leaflet/dist/leaflet.css';
import L from 'leaflet';
import axios from 'axios';

// Fix Leaflet default icons
delete L.Icon.Default.prototype._getIconUrl;
L.Icon.Default.mergeOptions({
  iconRetinaUrl: 'https://cdnjs.cloudflare.com/ajax/libs/leaflet/1.7.1/images/marker-icon-2x.png',
  iconUrl: 'https://cdnjs.cloudflare.com/ajax/libs/leaflet/1.7.1/images/marker-icon.png',
  shadowUrl: 'https://cdnjs.cloudflare.com/ajax/libs/leaflet/1.7.1/images/marker-shadow.png',
});

// Create a custom icon for the destination marker
const destinationIcon = new L.Icon({
  iconUrl: 'https://cdnjs.cloudflare.com/ajax/libs/leaflet/1.7.1/images/marker-icon.png',
  iconRetinaUrl: 'https://cdnjs.cloudflare.com/ajax/libs/leaflet/1.7.1/images/marker-icon-2x.png',
  shadowUrl: 'https://cdnjs.cloudflare.com/ajax/libs/leaflet/1.7.1/images/marker-shadow.png',
  iconSize: [25, 41],
  iconAnchor: [12, 41],
  popupAnchor: [1, -34],
  shadowSize: [41, 41],
  className: 'destination-marker' // We'll use this to style the marker
});

// Component to track map movements and update position
function TrackMapMovement({ setPosition, isTrackingEnabled }) {
  useMapEvents({
    moveend: (event) => {
      if (isTrackingEnabled) {
        const map = event.target;
        const center = map.getCenter();
        setPosition([center.lat, center.lng]);
      }
    },
  });
  return null;
}

function UpdateMapView({ position }) {
  const map = useMap();
  useEffect(() => {
    map.setView(position, map.getZoom());
  }, [position, map]);
  return null;
}

function MapView({ latitude = 32.075249, longitude = 34.807137 }) {
  const [position, setPosition] = useState([latitude, longitude]);
  const [destination, setDestination] = useState(null);
  const [isTrackingEnabled, setIsTrackingEnabled] = useState(true);
  const [searchQuery, setSearchQuery] = useState('');
  const [searchError, setSearchError] = useState(null);
  const [showDirections, setShowDirections] = useState(false);

  // Add CSS for the destination marker
  useEffect(() => {
    // Create a style element
    const style = document.createElement('style');
    // Add a CSS rule for the destination marker
    style.innerHTML = `
      .destination-marker {
        filter: hue-rotate(120deg); /* This will make the blue marker appear greenish */
      }
    `;
    // Append the style element to the document head
    document.head.appendChild(style);

    // Clean up
    return () => {
      document.head.removeChild(style);
    };
  }, []);

  useEffect(() => {
    setPosition([latitude, longitude]);
  }, [latitude, longitude]);

  const handleSearch = async () => {
    if (!searchQuery.trim()) return;

    try {
      setSearchError(null);
      const response = await axios.get(
        `https://nominatim.openstreetmap.org/search?q=${encodeURIComponent(searchQuery)}&format=json`
      );
      if (response.data.length > 0) {
        const { lat, lon } = response.data[0];
        setPosition([parseFloat(lat), parseFloat(lon)]);
      } else {
        setSearchError('Location not found.');
      }
    } catch (error) {
      setSearchError('Error fetching location. Please try again.');
    }
  };

  const handleSetDestination = async () => {
    if (!searchQuery.trim()) return;

    try {
      setSearchError(null);
      const response = await axios.get(
        `https://nominatim.openstreetmap.org/search?q=${encodeURIComponent(searchQuery)}&format=json`
      );
      if (response.data.length > 0) {
        const { lat, lon } = response.data[0];
        const newDestination = [parseFloat(lat), parseFloat(lon)];
        setDestination(newDestination);
        console.log("Destination set to:", newDestination);

        // Optionally show directions flag
        setShowDirections(true);
      } else {
        setSearchError('Destination not found.');
      }
    } catch (error) {
      setSearchError('Error fetching destination. Please try again.');
      console.error("Destination error:", error);
    }
  };

  return (
    <div style={{ height: '100vh', width: '100%' }}>
      <div style={{ position: 'absolute', top: 10, left: 10, zIndex: 1000, backgroundColor: 'rgba(255,255,255,0.8)', padding: '10px', borderRadius: '5px' }}>
        <button
          onClick={() => setIsTrackingEnabled((prev) => !prev)}
          style={{
            padding: '10px',
            backgroundColor: isTrackingEnabled ? 'green' : 'red',
            color: 'white',
            border: 'none',
            borderRadius: '5px',
            cursor: 'pointer',
            marginBottom: '10px',
            width: '100%'
          }}
        >
          {isTrackingEnabled ? 'Disable Tracking' : 'Enable Tracking'}
        </button>
        <div>
          <input
            type="text"
            placeholder="Search for a location"
            value={searchQuery}
            onChange={(e) => setSearchQuery(e.target.value)}
            style={{
              padding: '5px',
              width: '200px',
              marginRight: '5px',
              borderRadius: '5px',
              border: '1px solid #ccc',
              marginBottom: '5px'
            }}
          />
          <div style={{ display: 'flex', gap: '5px' }}>
            <button
              onClick={handleSearch}
              style={{
                padding: '5px 10px',
                backgroundColor: 'blue',
                color: 'white',
                border: 'none',
                borderRadius: '5px',
                cursor: 'pointer',
                flex: 1
              }}
            >
              Search
            </button>
            <button
              onClick={handleSetDestination}
              style={{
                padding: '5px 10px',
                backgroundColor: 'orange',
                color: 'white',
                border: 'none',
                borderRadius: '5px',
                cursor: 'pointer',
                flex: 1
              }}
            >
              Set Destination
            </button>
          </div>
        </div>
        {searchError && <p style={{ color: 'red', marginTop: '5px', fontSize: '14px' }}>{searchError}</p>}

        {/* Display destination information if available */}
        {destination && (
          <div style={{ marginTop: '10px', fontSize: '14px' }}>
            <p><strong>Destination set:</strong> {searchQuery}</p>
            <p><strong>Coordinates:</strong> {destination[0].toFixed(4)}, {destination[1].toFixed(4)}</p>
          </div>
        )}
      </div>

      <MapContainer center={position} zoom={13} style={{ height: '100%', width: '100%' }} zoomControl={false}>
        <TileLayer
          url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
          attribution='&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors'
        />
        <UpdateMapView position={position} />
        <TrackMapMovement setPosition={setPosition} isTrackingEnabled={isTrackingEnabled} />

        {/* Current position marker */}
        <Marker position={position}>
          <Popup>
            Your location: {position[0].toFixed(6)}, {position[1].toFixed(6)}
          </Popup>
        </Marker>

        {/* Destination marker with custom icon */}
        {destination && (
          <Marker position={destination} icon={destinationIcon}>
            <Popup>
              <div>
                <strong>Destination:</strong> {searchQuery}<br/>
                <strong>Coordinates:</strong> {destination[0].toFixed(6)}, {destination[1].toFixed(6)}
              </div>
            </Popup>
          </Marker>
        )}
      </MapContainer>
    </div>
  );
}

export default MapView;
