import React, { useState, useEffect } from 'react';
import './App.css';
import MapView from './MapView';
import StationArrivals from './components/data-display/StationArrivals';
import TransportControls from './components/controls/TransportControls';
import { fetchStationArrivals, extractVehicleMarkers, fetchLineShape } from './services/transport-api';

function CombinedTransportApp() {
  const [siriData, setSiriData] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [stationCode, setStationCode] = useState('26472'); // Default station code
  const [lineNumber, setLineNumber] = useState('60'); // Default line number
  const [routeShape, setRouteShape] = useState(null);
  const [routeDirection, setRouteDirection] = useState('0'); // 0 = outbound, 1 = inbound

  // Add state to control vehicle markers visibility
  const [showVehicleMarkers, setShowVehicleMarkers] = useState(false);
  const [vehicleMarkers, setVehicleMarkers] = useState([]);

  // Map state
  const [mapCenter, setMapCenter] = useState([32.07, 34.83]); // Default center around Tel Aviv
  const [stops, setStops] = useState([]);

  const fetchStationData = async () => {
    setLoading(true);
    setError(null);

    try {
      const data = await fetchStationArrivals(stationCode);
      setSiriData(data);

      // Extract vehicle markers from SIRI data
      const markers = extractVehicleMarkers(data);
      setVehicleMarkers(markers);

      setLoading(false);
    } catch (err) {
      console.error('Error fetching arrivals:', err);
      setError(`Connection error: ${err.message}. Make sure the API server is running.`);
      setLoading(false);
    }
  };

  const handleFetchLineShape = async () => {
    setLoading(true);
    setError(null);
    try {
      // Ensure the line number is valid
      if (!lineNumber.trim()) {
        setError("Please enter a valid line number");
        setLoading(false);
        return;
      }

      console.log(`Fetching shape for line ${lineNumber} in direction ${routeDirection}`);

      // Try direct API call first
      try {
        const response = await fetch(`/api/gtfs-api.php?action=getLineShape&line=${lineNumber}`);
        if (!response.ok) {
          throw new Error(`Server returned ${response.status}: ${response.statusText}`);
        }

        const data = await response.json();

        if (data.error) {
          setError(data.error);
          setRouteShape(null);
        } else {
          // Use the selected direction or default to '0' if not available
          const directionData = data[routeDirection] || data['0'] || [];
          setRouteShape(directionData);

          // If we have shape data, center the map on the first point
          if (directionData.length > 0) {
            setMapCenter(directionData[0]);
          }
        }
      } catch (err) {
        // Try fallback API
        console.warn("Primary API failed, trying fallback", err);

        try {
          const fallbackResponse = await fetch(`/gtfs-api.php?action=getLineShape&line=${lineNumber}`);
          if (!fallbackResponse.ok) {
            throw new Error(`Fallback server returned ${fallbackResponse.status}`);
          }

          const fallbackData = await fallbackResponse.json();
          if (fallbackData.error) {
            throw new Error(fallbackData.error);
          }

          const directionData = fallbackData[routeDirection] || fallbackData['0'] || [];
          setRouteShape(directionData);

          if (directionData.length > 0) {
            setMapCenter(directionData[0]);
          }
        } catch (fallbackErr) {
          console.error("Both API paths failed:", fallbackErr);
          throw new Error("All API paths failed for line shape request");
        }
      }
    } catch (err) {
      console.error('Error fetching line shape:', err);
      setError(`Connection error: ${err.message}`);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    // Fetch initial data
    fetchStationData();
  }, [stationCode]);

  return (
    <div className="combined-app">
      <div className="app-header">
        <h1>Israel Public Transportation Tracker</h1>
      </div>

      <TransportControls 
        stationCode={stationCode}
        setStationCode={setStationCode}
        fetchStationData={fetchStationData}
        lineNumber={lineNumber}
        setLineNumber={setLineNumber}
        routeDirection={routeDirection}
        setRouteDirection={setRouteDirection}
        fetchLineShape={handleFetchLineShape}
        showVehicleMarkers={showVehicleMarkers}
        setShowVehicleMarkers={setShowVehicleMarkers}
      />

      <div className="main-content">
        <div className="map-section">
          <MapView
            latitude={mapCenter[0]}
            longitude={mapCenter[1]}
            mapCenter={mapCenter}
            vehicleMarkers={showVehicleMarkers ? vehicleMarkers : []}
            routeShape={routeShape}
            stops={stops}
            onDestinationSet={() => {}}  // Placeholder
            center={mapCenter}
          />
        </div>
        <div className="data-section">
          <StationArrivals 
            siriData={siriData} 
            loading={loading} 
            error={error} 
            stationCode={stationCode}
          />
        </div>
      </div>
    </div>
  );
}

export default CombinedTransportApp;
