import './App.css';
import axios from 'axios';
import { useEffect, useState, useRef } from 'react';

function App() {
  const isInitialRender = useRef(true);
  const [Siri, setSiriData] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  useEffect(() => {
    if (isInitialRender.current) {
      isInitialRender.current = false;
      return;
    }
  }, []);

  const fetchData = () => {
    setLoading(true);
    setError(null);
    console.group('Fetch Data Process');
    console.log('Starting fetch at:', new Date().toISOString());
    axios({
      method: 'get',
      url: process.env.REACT_APP_API_URL || 'https://localhost/publicTransportation.php',
      timeout: 10000,
    })
      .then((res) => {
        console.log('Full Axios Response:', res);
        console.log('Response Data:', res.data);
        const siriData = res.data?.Siri || {}; // Store the entire Siri object
        console.log('Extracted Siri Data:', siriData);
        setSiriData(siriData);
        setLoading(false);
      })
      .catch((err) => {
        console.error('Complete Error Object:', err);
        console.log('Error Details:', {
          message: err.message,
          code: err.code,
          response: err.response,
          request: err.request,
        });
        const errorMessage = err.response
          ? `Server responded with status ${err.response.status}`
          : err.request
          ? 'Network error: Unable to reach the server'
          : 'An unexpected error occurred';
        setError(errorMessage);
        setLoading(false);
      })
      .finally(() => {
        console.groupEnd();
      });
  };

  useEffect(() => {
    console.log('Initial Render - Fetching Data');
    fetchData();
  }, []);

  useEffect(() => {
    console.log('Siri State Updated:', Siri);
  }, [Siri]);

  function VehicleDetails({ journey }) {
    const vehicleLocation = journey.VehicleLocation || {}; // Fallback to an empty object
    return (
      <div className="vehicle-details-container">
        <div className="details-row">
          <label className="label">Line:</label>
          <span className="value">{journey.PublishedLineName}</span>
        </div>
        <div className="details-row">
          <label className="label">Direction:</label>
          <span className="value">{journey.DirectionRef}</span>
        </div>
        <div className="details-row">
          <label className="label">Expected Arrival:</label>
          <span className="value">{journey.MonitoredCall?.ExpectedArrivalTime || 'N/A'}</span>
        </div>
        <div className="details-row">
          <label className="label">Distance From Stop:</label>
          <span className="value">{journey.MonitoredCall?.DistanceFromStop ? `${journey.MonitoredCall.DistanceFromStop} meters` : 'N/A'}</span>
        </div>
        <div className="details-row">
          <label className="label">Vehicle Location:</label>
          <span className="value">
            {vehicleLocation.Latitude && vehicleLocation.Longitude
              ? `${vehicleLocation.Latitude}, ${vehicleLocation.Longitude}`
              : 'Location not available'}
          </span>
        </div>
      </div>
    );
  }

  function ShowData({ Siri, loading, error }) {
    if (loading) return <h2>Loading...</h2>;
    if (error) return <h2>{error}</h2>;

    const serviceDelivery = Siri?.ServiceDelivery;
    if (!serviceDelivery) {
      console.warn('No ServiceDelivery found:', Siri);
      return <h2>No data available</h2>;
    }

    const monitoredVehicles =
      serviceDelivery.StopMonitoringDelivery[0]?.MonitoredStopVisit || [];

    if (monitoredVehicles.length === 0) {
      console.warn('No monitored vehicles found:', serviceDelivery);
      return <h2>No vehicles are currently being monitored</h2>;
    }

    console.log('Monitored Vehicles:', monitoredVehicles);
    return (
      <div>
        <h2>Monitored Vehicles:</h2>
        <ul>
          {monitoredVehicles.map((visit, index) => {
            const journey = visit.MonitoredVehicleJourney;
            const vehicleLocation = journey.VehicleLocation || {};
            return (
              <li key={visit.ItemIdentifier || index} className="vehicle-list-item">
                <p><strong>Line:</strong> {journey.PublishedLineName}</p>
                <p><strong>Direction:</strong> {journey.DirectionRef}</p>
                <p><strong>Expected Arrival:</strong> {journey.MonitoredCall?.ExpectedArrivalTime || 'N/A'}</p>
                <p><strong>Distance From Stop:</strong> {journey.MonitoredCall?.DistanceFromStop ? `${journey.MonitoredCall.DistanceFromStop} meters` : 'N/A'}</p>
                <p><strong>Vehicle Location:</strong> {vehicleLocation.Latitude && vehicleLocation.Longitude
                  ? `${vehicleLocation.Latitude}, ${vehicleLocation.Longitude}`
                  : 'Location not available'}</p>
              </li>
            );
          })}
        </ul>
      </div>
    );
  }

  return (
    <div className="App">
      <button
        className="refresh-button"
        onClick={fetchData}
        disabled={loading}
      >
        {loading ? 'Refreshing...' : 'Refresh'}
      </button>
      <div className="content">
        <div className="left-side">
          <ShowData Siri={Siri} loading={loading} error={error} />
        </div>
        <div className="right-side">
          <h2>
            {/* Remove Siri.map since Siri is now an object */}
            {Siri.ServiceDelivery ? (
              <div>
                <p>ServiceDelivery data is available.</p>
                {/* Add any additional logic to display specific data if needed */}
              </div>
            ) : (
              <p>No ServiceDelivery data available.</p>
            )}
          </h2>
        </div>
      </div>
    </div>
  );
}

export default App;
