import { useEffect, useState } from 'react';
import './App.css';
import { MapContainer, TileLayer, Marker, Popup, useMapEvents, useMap, Polyline } from 'react-leaflet';
import 'leaflet/dist/leaflet.css';
import L from 'leaflet';
import axios from 'axios';
import polyline from '@mapbox/polyline'; 

delete L.Icon.Default.prototype._getIconUrl;
L.Icon.Default.mergeOptions({
  iconRetinaUrl: 'https://cdnjs.cloudflare.com/ajax/libs/leaflet/1.7.1/images/marker-icon-2x.png',
  iconUrl: 'https://cdnjs.cloudflare.com/ajax/libs/leaflet/1.7.1/images/marker-icon.png',
  shadowUrl: 'https://cdnjs.cloudflare.com/ajax/libs/leaflet/1.7.1/images/marker-shadow.png',
});
const destinationIcon = new L.Icon({
  iconUrl: 'https://cdnjs.cloudflare.com/ajax/libs/leaflet/1.7.1/images/marker-icon.png',
  iconRetinaUrl: 'https://cdnjs.cloudflare.com/ajax/libs/leaflet/1.7.1/images/marker-icon-2x.png',
  shadowUrl: 'https://cdnjs.cloudflare.com/ajax/libs/leaflet/1.7.1/images/marker-shadow.png',
  iconSize: [25, 41],
  iconAnchor: [12, 41],
  popupAnchor: [1, -34],
  shadowSize: [41, 41],
  className: 'destination-marker' 
});
const centerIcon = new L.Icon({
  iconUrl: 'https://raw.githubusercontent.com/pointhi/leaflet-color-markers/master/img/marker-icon-green.png',
  iconRetinaUrl: 'https://raw.githubusercontent.com/pointhi/leaflet-color-markers/master/img/marker-icon-2x-green.png',
  shadowUrl: 'https://cdnjs.cloudflare.com/ajax/libs/leaflet/1.7.1/images/marker-shadow.png',
  iconSize: [25, 41],
  iconAnchor: [12, 41],
  popupAnchor: [1, -34],
  shadowSize: [41, 41],
  className: 'center-marker'
});

function TrackMapMovement({ setMapCenter }) {
  useMapEvents({
    moveend: (event) => {
      const map = event.target;
      const center = map.getCenter();
      setMapCenter([center.lat, center.lng]); // Only update map center
    },
  });
  return null;
}

function UpdateMapView({ position }) {
  const map = useMap();
  useEffect(() => {
    map.setView(position, map.getZoom());
  }, [position, map]);
  return null;
}

function MapView({ 
  latitude, 
  longitude, 
  destination, 
  onDestinationSet, 
  startingPoint // Receive startingPoint as an array
}) {
  const [position, setPosition] = useState([latitude, longitude]); // Initialize with latitude and longitude
  const [positionAddress, setPositionAddress] = useState('');
  const [destinationAddress, setDestinationAddress] = useState('');
  const [route, setRoute] = useState(null);
  const [mapCenter, setMapCenter] = useState([latitude, longitude]); // Track map center

  // Add missing state variables
  const [searchQuery, setSearchQuery] = useState(''); // For search input
  const [searchError, setSearchError] = useState(null); // For search errors
  const [isTrackingEnabled, setIsTrackingEnabled] = useState(true); // For tracking toggle

  // Add CSS for the destination marker
  useEffect(() => {
    // Create a style element
    const style = document.createElement('style');
    // Add a CSS rule for the destination marker
    style.innerHTML = `
      .destination-marker {
        filter: hue-rotate(120deg); /* This will make the blue marker appear greenish */
      }
    `;
    // Append the style element to the document head
    document.head.appendChild(style);

    // Clean up
    return () => {
      document.head.removeChild(style);
    };
  }, []);

  useEffect(() => {
    // Update position only when latitude or longitude props change
    setPosition([latitude, longitude]); // Ensure starting point is set correctly
    fetchAddress(latitude, longitude, setPositionAddress); // Fetch the correct address for the starting point
  }, [latitude, longitude]);

  useEffect(() => {
    if (destination) {
      fetchAddress(destination[0], destination[1], setDestinationAddress); // Update destination address
    }
  }, [destination]);

  useEffect(() => {
    // Center map between start and destination when both are set
    if (position && destination) {
      const midLat = (position[0] + destination[0]) / 2;
      const midLng = (position[1] + destination[1]) / 2;
      setMapCenter([midLat, midLng]);
    }
  }, [position, destination]);

  const simplifyAddress = (address) => {
    const parts = address.split(',').slice(0, 2); // Keep only the first two elements
    return parts.join(',').trim();
  };

  const fetchAddress = async (lat, lon, setAddress) => {
    try {
      // Add accept-language and countrycodes parameters to ensure correct region
      const response = await axios.get(
        `https://nominatim.openstreetmap.org/reverse?lat=${lat}&lon=${lon}&format=json&accept-language=he&addressdetails=1&countrycodes=il`
      );
      const fullAddress = response.data.display_name || 'Address not found';
      setAddress(simplifyAddress(fullAddress)); // Simplify the address
    } catch (error) {
      console.error('Error fetching address:', error);
      setAddress('Error fetching address');
    }
  };

  const handleSearch = async () => {
    if (!searchQuery.trim()) return;

    try {
      setSearchError(null);
      const response = await axios.get(
        `https://nominatim.openstreetmap.org/search?q=${encodeURIComponent(searchQuery)}&format=json`
      );
      if (response.data.length > 0) {
        const { lat, lon } = response.data[0];
        setPosition([parseFloat(lat), parseFloat(lon)]);
        fetchAddress(lat, lon, setPositionAddress); // Fetch address for the new position
      } else {
        setSearchError('Location not found.');
      }
    } catch (error) {
      setSearchError('Error fetching location. Please try again.');
    }
  };

  const handleSetDestination = async () => {
    if (!searchQuery.trim()) return;

    try {
      setSearchError(null);
      const response = await axios.get(
        `https://nominatim.openstreetmap.org/search?q=${encodeURIComponent(searchQuery)}&format=json`
      );
      if (response.data.length > 0) {
        const { lat, lon } = response.data[0];
        const newDestination = [parseFloat(lat), parseFloat(lon)];
        onDestinationSet(newDestination); // Notify App.js of the new destination
        fetchAddress(lat, lon, setDestinationAddress); // Update destination address
      } else {
        setSearchError('Destination not found.');
      }
    } catch (error) {
      setSearchError('Error fetching destination. Please try again.');
      console.error('Destination error:', error);
    }
  };

  const handleFindRoute = async () => {
    if (!destination) {
      setSearchError('Please set a destination first.');
      return;
    }

    const MAX_RETRIES = 30; // Maximum number of retries
    let attempts = 0;

    while (attempts < MAX_RETRIES) {
      try {
        setSearchError(null);
        const response = await axios.get(
          `https://api.openrouteservice.org/v2/directions/driving-car`,
          {
            params: {
              api_key: '5b3ce3597851110001cf62489ba3c6e8d4824449975eca1f205d2eec',
              start: `${position[1]},${position[0]}`, // Longitude, Latitude
              end: `${destination[1]},${destination[0]}`, // Longitude, Latitude
            },
          }
        );

        console.log('Route response:', response.data); // Log the full response for debugging

        if (response.data && response.data.features && response.data.features.length > 0) {
          const routeGeometry = response.data.features[0].geometry.coordinates; // Access geometry from features
          const decodedRoute = routeGeometry.map(([lon, lat]) => [lat, lon]); // Convert to Leaflet-compatible format
          setRoute(decodedRoute);
          return; // Exit the loop on success
        } else {
          setSearchError('No route found. Please try again with different locations.');
          console.error('No routes found in the response:', response.data);
          return; // Exit if no route is found
        }
      } catch (error) {
        attempts++;
        console.error(`Route error (attempt ${attempts}):`, error);

        if (attempts >= MAX_RETRIES) {
          if (error.response && error.response.status === 403) {
            setSearchError('Invalid API key or insufficient permissions.');
          } else if (error.response && error.response.status === 503) {
            setSearchError('Service unavailable. Please try again later.');
          } else {
            setSearchError('Error fetching route. Please try again.');
          }
        }
      }
    }
  };

  const handleSetStartPoint = () => {
    setPosition(mapCenter); // Set the starting point to the map center
    fetchAddress(mapCenter[0], mapCenter[1], setPositionAddress); // Update the address
  };

  const handleSetDestinationPoint = () => {
    if (onDestinationSet) {
      onDestinationSet(mapCenter); // Set the destination to the map center
      fetchAddress(mapCenter[0], mapCenter[1], setDestinationAddress); // Update the address
    } else {
      console.error('onDestinationSet is not defined');
    }
  };

  const handleCenterMap = () => {
    if (position && destination) {
      const midLat = (position[0] + destination[0]) / 2;
      const midLng = (position[1] + destination[1]) / 2;
      setMapCenter([midLat, midLng]); // Center map between start and destination
    } else {
      setMapCenter(position); // Default to current position
    }
  };

  return (
    <div style={{ height: '100vh', width: '100%', display: 'flex', flexDirection: 'column' }}>
      {/* Controls Section */}
      <div style={{ padding: '10px', backgroundColor: 'rgba(255,255,255,0.9)', zIndex: 1000 }}>
        <div style={{ display: 'flex', gap: '10px', marginBottom: '10px' }}>
          <button
            onClick={() => setIsTrackingEnabled((prev) => !prev)}
            style={{
              padding: '10px',
              backgroundColor: isTrackingEnabled ? 'green' : 'red',
              color: 'white',
              border: 'none',
              borderRadius: '5px',
              cursor: 'pointer',
              flex: 1,
            }}
          >
            {isTrackingEnabled ? 'Disable Tracking' : 'Enable Tracking'}
          </button>
          <button
            onClick={handleCenterMap}
            style={{
              padding: '10px',
              backgroundColor: '#333',
              color: 'white',
              border: 'none',
              borderRadius: '5px',
              cursor: 'pointer',
              flex: 1,
            }}
          >
            Center Map
          </button>
        </div>
        <div style={{ display: 'flex', gap: '10px', marginBottom: '10px' }}>
          <input
            type="text"
            placeholder="Search for a location"
            value={searchQuery}
            onChange={(e) => setSearchQuery(e.target.value)}
            style={{
              padding: '10px',
              flex: 2,
              borderRadius: '5px',
              border: '1px solid #ccc',
            }}
          />
          <button
            onClick={handleSearch}
            style={{
              padding: '10px',
              backgroundColor: 'blue',
              color: 'white',
              border: 'none',
              borderRadius: '5px',
              cursor: 'pointer',
              flex: 1,
            }}
          >
            Search
          </button>
          <button
            onClick={handleSetDestination}
            style={{
              padding: '10px',
              backgroundColor: 'orange',
              color: 'white',
              border: 'none',
              borderRadius: '5px',
              cursor: 'pointer',
              flex: 1,
            }}
          >
            Set Destination
          </button>
        </div>
        <button
          onClick={handleFindRoute}
          style={{
            padding: '10px',
            backgroundColor: 'purple',
            color: 'white',
            border: 'none',
            borderRadius: '5px',
            cursor: 'pointer',
            width: '100%',
          }}
        >
          Find Route
        </button>
        {searchError && <p style={{ color: 'red', marginTop: '10px', fontSize: '14px' }}>{searchError}</p>}
      </div>

      {/* Information Section */}
      <div style={{ display: 'flex', gap: '10px', padding: '10px', backgroundColor: 'rgba(255,255,255,0.9)', zIndex: 1000 }}>
        <div style={{ flex: 1, border: '1px solid #ccc', padding: '10px', borderRadius: '5px' }}>
          <h4>Start Location</h4>
          <p><strong>Address:</strong> {positionAddress || 'Loading...'}</p>
        </div>
        <div style={{ flex: 1, border: '1px solid #ccc', padding: '10px', borderRadius: '5px' }}>
          <h4>Destination</h4>
          <p><strong>Address:</strong> {destinationAddress || 'No destination set'}</p>
        </div>
      </div>

      {/* Map Section */}
      <div style={{ flex: 1 }}>
        <MapContainer center={mapCenter} zoom={13} style={{ height: '100%', width: '100%' }} zoomControl={false}>
          <TileLayer
            url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
            attribution='&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors'
          />
          <UpdateMapView position={mapCenter} />
          <TrackMapMovement setMapCenter={setMapCenter} />
          {/* Current position marker */}
          <Marker position={position}>
            <Popup>
              Your location: {positionAddress}<br/>
              Coordinates: {position[0].toFixed(6)}, {position[1].toFixed(6)}
            </Popup>
          </Marker>
          {/* Destination marker with custom icon */}
          {destination && (
            <Marker position={destination} icon={destinationIcon}>
              <Popup>
                <div>
                  <strong>Destination:</strong> {destinationAddress}<br/>
                  <strong>Coordinates:</strong> {destination[0].toFixed(6)}, {destination[1].toFixed(6)}
                </div>
              </Popup>
            </Marker>
          )}
          {/* Route polyline */}
          {route && (
            <Polyline positions={route} color="blue" />
          )}
        </MapContainer>
      </div>
    </div>
  );
}

export default MapView;
